def Cal_Shifted_index(mini_text):
    p=english_letter_freq_list()
    #print(p)
    f=freqTable(mini_text)
    n=countLetters(mini_text)
    m=[0.0]*26
    for j in range(26):
        for i in range(26):
            #m.append((p[i]*f[i])/(100*n))
            m[j]=m[j]+(p[i]*f[(i+j)%26])/(100*n)
        m[j]=abs(m[j]-0.065)
    #print(m)    
    return m

def shiftcipher(key, cipher_text):
    plaintext = ''
    for symbol in cipher_text:
        if symbol in Letters:
            num = Letters.find(symbol)
            num = num - key
            if num < 0:
                num = num + len(Letters)
            plaintext = plaintext + Letters[num]
        else:
            plaintext = plaintext + symbol
    #print('Decrypyed plain text is: \n' + plaintext)
    return(plaintext)


def subscipher(cipher_text):
    key=input('enter the 26-letter substitution key:').lower()
    if len(key)==26:
        dicc=dict(zip(Letters , key))
        #print(dicc)
        flipped = {v: k for k, v in dicc.items()}
        #print(flipped)
        plaintext=''.join(flipped[l] for l in cipher_text)
        print('palintext is given as: \n', plaintext)
    else:
        print('length of the key is not correct\n')
    return()




