def FindKeyElement(substrings):
    mg=[]
    for i in range(len(substrings)):
        m = Cal_Shifted_index(substrings[i])
        mg.append(m.index(min(m)))
        
    return (mg)



def VigCipher(keyElements, substrings):
    
    plainttext=[]
    dec_text=''
    for i in range(len(keyElements)):
        plainttext.append(shiftcipher(keyElements[i], substrings[i]))
    #print(plainttext)
    for j in range((len(substrings[0]))-1):
        for i in range(len(substrings)):
            dec_text=dec_text+plainttext[i][j]
    #print("The decrypted text is:",dec_text)
    return dec_text


